// SPDX-License-Identifier: MIT
pragma solidity ^0.8.11;
pragma experimental ABIEncoderV2;

import "./User.sol";
import "./Product.sol";
import "./OrderRequestInfo.sol";

contract Retailer {
    User user;

    constructor(address _userAddress) {
        user = User(_userAddress);
    }

    modifier onlyRetailer() {
        require(user.getUserRole(msg.sender) == 3, "ORCC");
        _;
    }

    // Track sale status of each product batch.
    mapping(address => mapping(address => SaleStatus)) ProductSaleStatus;
    mapping(address => uint256) SoldOutCount;
    mapping(address => uint256) DamageCount;
    mapping(address => uint256) ApprovedCount;
    mapping(address => uint256) RejectedCount;

    //Enum Status - To hold different product sale status
    enum SaleStatus {
        approved,
        damage,
        sold
    }

    //Event to capture & dispatch product batch detalils & updated status on received
    event UpdatedProductBatchStatusEvt(
        address indexed productId,
        uint256 status,
        uint256 approvedCount,
        uint256 rejectedCount
    );

    //Event to capture & dispatch product sale status
    event UpdatedProductSaleStatusEvt(
        address indexed productId,
        uint256 updatedQty,
        uint256 status,
        uint256 soldOutCount,
        uint256 damageCount
    );

    //Event to dispatch order request status after batch approved
    event UpdatedOrderStatusEvt(
        address indexed orderRequestId,
        uint256 orderStatus,
        uint256 submissionTimestamp,
        uint256 orderModifiedDate
    );

    //Reject/Approve product batch by Retailer
    //@param _productId Product Id address
    //@param _isApprove Boolean flag for approve/reject
    function updateProductBatchStatusOnReceive(
        address _productId,
        bool _isApprove,
        string memory _reason
    ) public onlyRetailer {
        bool result = _isApprove
            ? Product(_productId).approveProductBatch()
            : Product(_productId).rejectProductBatch(_reason);
        if (result) {
            _isApprove
                ? ApprovedCount[msg.sender] = ApprovedCount[msg.sender] + 1
                : RejectedCount[msg.sender] = RejectedCount[msg.sender] + 1;
            if (_isApprove) {
                address _orderRequestId = Product(_productId)
                    .getOrderRequestId();
                OrderRequestInfo(_orderRequestId).complete();

                //Dispatching event
                emit UpdatedOrderStatusEvt(
                    _orderRequestId,
                    OrderRequestInfo(_orderRequestId).getOrderRequestStatus(),
                    OrderRequestInfo(_orderRequestId)
                        .getOrderSubmissionTimestamp(),
                    OrderRequestInfo(_orderRequestId).getOrderModifiedDate()
                );
            }
            //Dispatching event
            emit UpdatedProductBatchStatusEvt(
                _productId,
                Product(_productId).getProductBatchStatus(),
                ApprovedCount[msg.sender],
                RejectedCount[msg.sender]
            );
        }
    }

    //Method to update product sale status
    //@param _productId  Address Product Id
    //@param _saleStatus uint256 Product sale status
    function updateSaleStatus(
        address _productId,
        uint256 _saleStatus,
        uint256 _qty
    ) public onlyRetailer {
        if (Product(_productId).getProductBatchStatus() != 6) {
            revert("PROD_NOT_YET_APRVD");
        }
        ProductBasicInfo memory _productBasicInfo = Product(_productId)
            .getProductBasicInfo();
        if (_qty > _productBasicInfo.quantity) {
            revert("INVALID_QTY");
        }
        _productBasicInfo.quantityRemain = _productBasicInfo.quantity - _qty;
        Product(_productId).updateQuantity(_productBasicInfo.quantityRemain);
        if (_productBasicInfo.quantityRemain == 0) {
            ProductSaleStatus[msg.sender][_productId] = SaleStatus(_saleStatus);
        }
        if (_saleStatus == 2) {
            SoldOutCount[msg.sender] = SoldOutCount[msg.sender] + 1;
        } else if (_saleStatus == 1) {
            DamageCount[msg.sender] = DamageCount[msg.sender] + 1;
        }
        emit UpdatedProductSaleStatusEvt(
            _productId,
            _productBasicInfo.quantityRemain,
            uint256(ProductSaleStatus[msg.sender][_productId]),
            SoldOutCount[msg.sender],
            DamageCount[msg.sender]
        );
    }

    //Get sale status of product
    //@param _productId Address Product Id
    function getProductSaleSatus(address _productId)
        public
        onlyRetailer
        returns (uint256)
    {
        return uint256(ProductSaleStatus[msg.sender][_productId]);
    }

    //Get product sale status total count
    function getSoldOutCount(address _retailer) public view returns (uint256) {
        return SoldOutCount[_retailer];
    }

    //Get product damage total count
    function getDamageCount(address _retailer) public view returns (uint256) {
        return DamageCount[_retailer];
    }

    //Get product approved total count
    function getApprovedCount(address _retailer) public view returns (uint256) {
        return ApprovedCount[_retailer];
    }

    //Get product rejected total count
    function getRejectedCount(address _retailer) public view returns (uint256) {
        return RejectedCount[_retailer];
    }
}
